mapper.update:
update tbl_music 
set title = #{title}, artist = #{artist}, link = #{link}, updateDate = now()
where mno = #{mno}

mapper.select:
select mno, pno, title, artist, link, addDate, updateDate  from(
select mno, pno, title, artist, link, addDate, updateDate from tbl_music
		where pno = #{pno} 
		order by mno desc) as tbl_music
		limit #{cri.skip}, #{cri.amount}
